using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;

namespace WarhammerEmu.GameServer
{
    public class CharacterResponse : IPacketHandler
    {
        [PacketHandlerAttribute((int)Opcodes.F_PLAYER_ENTER_FULL)]
        public static void F_PLAYER_ENTER_FULL(Connection conn, PacketIn packet)
        {
            PacketOut Out = new PacketOut((byte)Opcodes.S_PID_ASSIGN);
            Out.WriteUInt16R(15);
            conn.SendTCP(Out);

            Log.Trace("F_PLAYER_ENTER_FULL");
        }


        [PacketHandlerAttribute((int)Opcodes.F_REQUEST_CHAR)]
        public static void F_REQUEST_CHAR(Connection conn, PacketIn packet)
        {
            UInt16 Operation = packet.GetUint16();

            if (Operation == 0x2D58)
            {
                PacketOut Out = new PacketOut((byte)Opcodes.F_REQUEST_CHAR_ERROR);
                Out.WriteByte(2);
                conn.SendTCP(Out);
            }

            else
            {
                PacketOut Out = new PacketOut((byte)Opcodes.F_REQUEST_CHAR_RESPONSE);

                Out.WriteHexStringBytes("");
                conn.SendTCP(Out);
            }
            Log.Trace("F_REQUEST_CHAR");
        }

        [PacketHandlerAttribute((int)Opcodes.F_DUMP_ARENAS_LARGE)]
        public static void F_DUMP_ARENAS_LARGE(Connection conn, PacketIn packet)
        {
            PacketOut Out = new PacketOut((byte)Opcodes.F_WORLD_ENTER);
            Out.WriteUInt16(0x0608); // TODO
            Out.Fill(0, 20);
            Out.WriteString("38699", 5);
            Out.WriteString("38700", 5);
            Out.WriteString("0.0.0.0", 20);
            conn.SendTCP(Out);

            Log.Trace("F_DUMP_ARENAS_LARGE");
        }

        [PacketHandlerAttribute((int)Opcodes.F_OPEN_GAME)]
        public static void F_OPEN_GAME(Connection conn, PacketIn packet)
        {

            PacketOut Out = new PacketOut((byte)Opcodes.S_GAME_OPENED);
            Out.WriteByte(0);
            conn.SendTCP(Out);
            Log.Trace("F_OPEN_GAME");
        }

        [PacketHandlerAttribute((int)Opcodes.F_REQUEST_WORLD_LARGE)]
        public static void F_REQUEST_WORLD_LARGE(Connection conn, PacketIn packet)
        {
            PacketOut Out = new PacketOut((byte)Opcodes.S_WORLD_SENT);
            Out.WriteByte(0);
            conn.SendTCP(Out);
            Log.Trace("F_REQUEST_WORLD_LARGE");
        }

        [PacketHandlerAttribute((int)Opcodes.F_INIT_PLAYER)]
        public static void F_INIT_PLAYER(Connection conn, PacketIn packet)
        {
            PacketOut Out = new PacketOut((byte)Opcodes.S_PLAYER_INITTED);
            Out.WriteHexStringBytes("00CA00000028D5BF1D7F0000000CF824000CAFC7051700020000000000080001001A000000000000084261646C616E6473000000");
            conn.SendTCP(Out);
            Log.Trace("F_INIT_PLAYER");
        }
    }
}
